  number: number;
  name: string;
  scratched: boolean;
  selection_id?: number;
  odds: Record<string, OddsData>;
  jockey?: string;
  trainer?: string;
}

// --- Race Interface ---
// This interface matches the shape of the data returned by the API for the dashboard.
export interface Race {
  id: string;
  venue: string;
  race_number: number;
  start_time: string;
  runners: Runner[];
  source: string;
  qualification_score?: number;
  distance?: string;
  surface?: string;
  favorite?: Runner;
  isErrorPlaceholder?: boolean;
  errorMessage?: string;
}

// --- API Response Interfaces ---
export interface SourceInfo {
  name: string;
  status: 'SUCCESS' | 'FAILED' | 'CONFIG_ERROR' | 'PENDING';
  racesFetched: number;
  fetchDuration: number;
  errorMessage?: string;
  attemptedUrl?: string;
}

export interface AdapterError {
  adapterName: string;
  errorMessage: string;
  attemptedUrl?: string;
}

