        Write-Host "  ❌ $file"
        $missing += $file
    }
}

if ($missing.Count -gt 0) {
    Write-Host "`n❌ FATAL: Missing files:" -ForegroundColor Red
    $missing | ForEach-Object { Write-Host "  - $_" }
