# scripts/generate_report.ps1

[CmdletBinding()]
param (
    [string]$JsonInputPath = "qualified_races.json",
    [string]$HtmlOutputPath = "race-report.html"
)

Write-Host "Generating HTML report from $JsonInputPath..." -ForegroundColor Cyan

if (-not (Test-Path $JsonInputPath)) {
    Write-Host "❌ ERROR: Input JSON file not found at $JsonInputPath" -ForegroundColor Red
    exit 1
}

$races = Get-Content $JsonInputPath | ConvertFrom-Json
$timestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss UTC"

$html = @"
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>🐴 Fortuna - Filtered Race Report</title>
  <style>
    * { margin: 0; padding: 0; box-sizing: border-box; }
    html { scroll-behavior: smooth; }

    body {
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
      background: linear-gradient(135deg, #0f1419 0%, #1a1f2e 50%, #16213e 100%);
      color: #e2e8f0;
      padding: 2rem;
      line-height: 1.6;
      min-height: 100vh;
    }

    .container {
      max-width: 1400px;
      margin: 0 auto;
    }

    header {
      text-align: center;
      margin-bottom: 3rem;
      background: linear-gradient(135deg, rgba(15, 52, 96, 0.5), rgba(0, 255, 136, 0.1));
      border: 2px solid #00ff88;
      border-radius: 12px;
      padding: 3rem 2rem;
      box-shadow: 0 8px 32px rgba(0, 255, 136, 0.2);
    }

    h1 {
      color: #00ff88;
      font-size: 2.8rem;
      margin-bottom: 0.5rem;
      text-shadow: 0 0 10px rgba(0, 255, 136, 0.5);
    }

    .subtitle {
      color: #a0aec0;
      font-size: 1rem;
      margin-top: 0.5rem;
    }

    .summary-box {
      background: rgba(0, 255, 136, 0.1);
      border-left: 4px solid #00ff88;
      border-radius: 8px;
      padding: 1.5rem 2rem;
      margin: 2rem 0;
      font-size: 1.1rem;
      text-align: center;
      color: #00ff88;
      font-weight: bold;
    }

    .race-card {
      background: linear-gradient(135deg, #0f3460 0%, #1a4d7a 100%);
      border: 1px solid rgba(0, 255, 136, 0.2);
      border-left: 4px solid #00ff88;
      border-radius: 12px;
      padding: 2rem;
      margin-bottom: 2rem;
      box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
      transition: all 0.3s ease;
    }

    .race-card:hover {
      transform: translateY(-4px);
      box-shadow: 0 8px 30px rgba(0, 255, 136, 0.25);
      border-color: rgba(0, 255, 136, 0.5);
    }

    .race-header {
      display: flex;
      justify-content: space-between;
      align-items: flex-start;
      margin-bottom: 1.5rem;
      border-bottom: 2px solid #00ff88;
      padding-bottom: 1rem;
      flex-wrap: wrap;
      gap: 1rem;
    }

    .race-title {
      font-size: 1.4rem;
      font-weight: bold;
      color: #00ff88;
    }

    .race-meta {
      color: #a0aec0;
      font-size: 0.9rem;
    }

    .runners-table {
      width: 100%;
      border-collapse: collapse;
      margin-top: 1rem;
    }

    .runners-table thead {
      background: rgba(0, 255, 136, 0.15);
      border-bottom: 2px solid #00ff88;
    }

    .runners-table th {
      padding: 1rem;
      text-align: left;
      font-weight: bold;
      color: #00ff88;
      text-transform: uppercase;
      font-size: 0.85rem;
      letter-spacing: 1px;
    }

    .runners-table td {
      padding: 0.75rem 1rem;
      border-bottom: 1px solid #1a3a52;
    }

