  const { bg, text } = getScoreStyling(score);
  return (
    <div className={`text-right ${text}`}>
      <p className="text-3xl font-bold">{score.toFixed(1)}</p>
      <p className="text-xs font-medium tracking-wider uppercase\">Score</p>
    </div>
  );
};

