'use client';

import React, { useState, useEffect } from 'react';
import type { Race, Runner } from '../types/racing';

// Local types removed, now importing from '../types/racing'

interface RaceCardProps {
  race: Race;
}

const Countdown: React.FC<{ startTime: string }> = ({ startTime }) => {
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const getCountdown = (startTimeStr: string) => {
    const postTime = new Date(startTimeStr);
    const diff = postTime.getTime() - currentTime.getTime();

    if (diff <= 0) return { text: "RACE COMPLETE", color: "text-gray-500" };

    const minutes = Math.floor(diff / 60000);
    const seconds = Math.floor((diff % 60000) / 1000).toString().padStart(2, '0');

    let color = "text-green-400";
    if (minutes < 2) color = "text-red-500 font-bold animate-pulse";
    else if (minutes < 10) color = "text-yellow-400";

    return { text: `${minutes}:${seconds} to post`, color };
  };

  const countdown = getCountdown(startTime);

  return (
    <span className={`font-mono text-sm ${countdown.color}`}>{countdown.text}</span>
  );
};

export const RaceCard: React.FC<RaceCardProps> = ({ race }) => {
  const activeRunners = race.runners.filter(r => !r.scratched);
  activeRunners.sort((a, b) => a.number - b.number);

  const getUniqueSourcesCount = (runners: Runner[]): number => {
    const sources = new Set();
    runners.forEach(runner => {
      if (runner.odds) {
        Object.keys(runner.odds).forEach(source => sources.add(source));
      }
    });
    return sources.size;
  };

  const getBestOdds = (runner: Runner): { odds: number, source: string } | null => {
    if (!runner.odds) return null;
  const validOdds = Object.values(runner.odds).filter(o => o.win !== null && o.win !== undefined && o.win < 999);
    if (validOdds.length === 0) return null;
  const best = validOdds.reduce((min, o) => (o.win ?? 999) < (min.win ?? 999) ? o : min);
    return { odds: best.win!, source: best.source };
  };

  return (
    <div className={`race-card-enhanced border rounded-lg p-4 bg-gray-800 shadow-lg hover:border-purple-500 transition-all ${race.qualification_score && race.qualification_score >= 80 ? 'card-premium' : 'border-gray-700'}`}>
      {/* Header with Smart Status Indicators */}
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-3">
          <div>
            <h2 className="text-2xl font-bold text-white">{race.venue}</h2>
            <div className="flex gap-2 text-sm text-gray-400">
              <span>Race {race.race_number}</span>
              <span>•</span>
              <Countdown startTime={race.start_time} />
            </div>
            {race.favorite && (
              <div className="flex items-center gap-2 mt-2 text-sm text-yellow-400">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" viewBox="0 0 20 20" fill="currentColor">
                  <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                </svg>
                <span className="font-semibold">Favorite: {race.favorite.name}</span>
              </div>
            )}
          </div>
        </div>

        {race.qualification_score && (
          <div className={`px-4 py-2 rounded-full text-center ${
            race.qualification_score >= 80 ? 'bg-red-500/20 text-red-400 border border-red-500/30' :
            race.qualification_score >= 60 ? 'bg-yellow-500/20 text-yellow-400 border border-yellow-500/30' :
            'bg-green-500/20 text-green-400 border border-green-500/30'
          }`}>
            <div className="font-bold text-lg">{race.qualification_score.toFixed(0)}%</div>
            <div className="text-xs">Score</div>
          </div>
        )}
      </div>

      {/* Race Conditions Grid */}
      <div className="grid grid-cols-4 gap-2 mb-4 p-3 bg-gray-800/50 rounded-lg">
        <div className="text-center">
          <div className="text-xs text-gray-400">Distance</div>
          <div className="text-sm font-semibold text-white">{race.distance || 'N/A'}</div>
        </div>
        <div className="text-center">
          <div className="text-xs text-gray-400">Surface</div>
          <div className="text-sm font-semibold text-white">{race.surface || 'Dirt'}</div>
        </div>
        <div className="text-center">
          <div className="text-xs text-gray-400">Field</div>
          <div className="text-sm font-semibold text-white">{activeRunners.length}</div>
        </div>
        <div className="text-center">
          <div className="text-xs text-gray-400">Sources</div>
          <div className="text-sm font-semibold text-white">{getUniqueSourcesCount(race.runners)}</div>
        </div>
      </div>

      {/* Interactive Runner Rows */}
      <div className="runners-table space-y-2">
        {activeRunners.map((runner, idx) => {
          const bestOddsInfo = getBestOdds(runner);
          return (
            <div key={runner.number} className="runner-row group hover:bg-purple-500/10 transition-all rounded-md p-3">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-4 flex-1">
                  <div className={`w-10 h-10 rounded-full flex items-center justify-center font-bold transition-all group-hover:scale-110 text-gray-900 shadow-lg ${idx === 0 ? 'bg-gradient-to-br from-yellow-400 to-yellow-600 shadow-yellow-500/50' : idx === 1 ? 'bg-gradient-to-br from-gray-300 to-gray-500 shadow-gray-400/50' : idx === 2 ? 'bg-gradient-to-br from-orange-400 to-orange-600 shadow-orange-500/50' : 'bg-gray-700 text-gray-300'}`}>
                    {runner.number}
                  </div>
                  <div className="flex flex-col">
                    <span className="font-bold text-white text-lg">{runner.name}</span>
                    <div className="flex gap-3 text-sm text-gray-400">
                      {runner.jockey && <span>J: {runner.jockey}</span>}
                      {runner.trainer && <span>T: {runner.trainer}</span>}
                    </div>
                  </div>
                </div>
                {bestOddsInfo && (
                  <div className="text-right">
                    <div className="text-2xl font-bold text-emerald-400">{bestOddsInfo.odds.toFixed(2)}</div>
                    <div className="text-xs text-gray-500">via {bestOddsInfo.source}</div>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

// web_platform/frontend/src/components/RaceFilters.tsx
'use client';

import { useState, useCallback } from 'react';
import { Settings, RotateCcw } from 'lucide-react';

interface FilterParams {
  maxFieldSize: number;
  minFavoriteOdds: number;
  minSecondFavoriteOdds: number;
}

export interface RaceFiltersProps {
  onParamsChange: (params: FilterParams) => void;
  isLoading: boolean;
  refetch: () => void;
}

const DEFAULT_PARAMS: FilterParams = {
  maxFieldSize: 10,
  minFavoriteOdds: 2.5,
  minSecondFavoriteOdds: 4.0,
};

export function RaceFilters({ onParamsChange, isLoading, refetch }: RaceFiltersProps) {
  const [params, setParams] = useState<FilterParams>(DEFAULT_PARAMS);
  const [isExpanded, setIsExpanded] = useState(false);

  // Handle individual parameter changes
  const handleChange = useCallback((key: keyof FilterParams, value: number) => {
    setParams(prev => {
      const updated = { ...prev, [key]: value };
      onParamsChange(updated);
      return updated;
    });
    // Debounce the refetch call
    const timer = setTimeout(() => {
      refetch();
    }, 500);
    return () => clearTimeout(timer);
  }, [onParamsChange, refetch]);

  // Reset to defaults
  const handleReset = useCallback(() => {
    setParams(DEFAULT_PARAMS);
    onParamsChange(DEFAULT_PARAMS);
    refetch();
  }, [onParamsChange, refetch]);

  return (
    <div className="bg-gradient-to-r from-slate-800 to-slate-900 rounded-lg p-4 mb-6 border border-slate-700">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-2">
          <Settings className="w-5 h-5 text-amber-500" />
          <h3 className="text-lg font-semibold text-white">Race Filters</h3>
        </div>
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="text-sm text-slate-400 hover:text-slate-200 transition"
        >
          {isExpanded ? 'Hide' : 'Show'}
        </button>
      </div>

      {isExpanded && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 pt-4 border-t border-slate-700">
          {/* Max Field Size */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-slate-300">
              Max Field Size
              <span className="text-amber-500 ml-2">{params.maxFieldSize}</span>
            </label>
            <input
              type="range"
              min="2"
              max="20"
              value={params.maxFieldSize}
              onChange={(e) => handleChange('maxFieldSize', parseInt(e.target.value))}
              disabled={isLoading}
              className="w-full accent-amber-500 cursor-pointer disabled:opacity-50"
            />
            <p className="text-xs text-slate-500">Filters races with larger fields</p>
          </div>

          {/* Min Favorite Odds */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-slate-300">
              Min Favorite Odds
              <span className="text-amber-500 ml-2">{params.minFavoriteOdds.toFixed(2)}</span>
            </label>
            <input
              type="range"
              min="1.5"
              max="5"
              step="0.1"
              value={params.minFavoriteOdds}
              onChange={(e) => handleChange('minFavoriteOdds', parseFloat(e.target.value))}
              disabled={isLoading}
              className="w-full accent-amber-500 cursor-pointer disabled:opacity-50"
            />
            <p className="text-xs text-slate-500">Higher = pickier favorites</p>
          </div>

          {/* Min Second Favorite Odds */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-slate-300">
              Min 2nd Favorite Odds
              <span className="text-amber-500 ml-2">{params.minSecondFavoriteOdds.toFixed(2)}</span>
            </label>
            <input
              type="range"
              min="2.0"
              max="8"
              step="0.1"
              value={params.minSecondFavoriteOdds}
              onChange={(e) => handleChange('minSecondFavoriteOdds', parseFloat(e.target.value))}
              disabled={isLoading}
              className="w-full accent-amber-500 cursor-pointer disabled:opacity-50"
            />
            <p className="text-xs text-slate-500">Higher = better odds separation</p>
          </div>

          {/* Reset Button */}
          <div className="md:col-span-3 flex justify-end pt-4 border-t border-slate-700">
            <button
              onClick={handleReset}
              disabled={isLoading}
              className="inline-flex items-center gap-2 px-4 py-2 bg-slate-700 hover:bg-slate-600 text-slate-200 rounded text-sm font-medium transition disabled:opacity-50"
            >
              <RotateCcw className="w-4 h-4" />
              Reset to Defaults
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

// web_platform/frontend/src/types/electron.d.ts

/**
 * This declaration file extends the global Window interface to include the
 * 'electronAPI' object exposed by the preload script. This provides
 * TypeScript with type information for the functions we're using for IPC.
 */
export {};

declare global {
  interface Window {
    electronAPI?: {
      /**
       * Asynchronously fetches the secure API key from the main process.
       * @returns {Promise<string|null>} A promise that resolves with the API key or null if not found.
       */
      getApiKey: () => Promise<string | null>;
      /**
       * Registers a callback for backend status updates from the main process.
       * @param callback The function to execute. Receives an object with state and logs.
       * @returns A function to unsubscribe the listener.
       */
      onBackendStatusUpdate: (callback: (status: { state: 'starting' | 'running' | 'error' | 'stopped'; logs: string[] }) => void) => () => void;

      /**
       * Sends a command to the main process to restart the backend executable.
       */
      restartBackend: () => void;

      /**
       * Asynchronously fetches the current backend status from the main process.
       * @returns {Promise<{ state: 'starting' | 'running' | 'error' | 'stopped'; logs: string[] }>}
       */
      getBackendStatus: () => Promise<{ state: 'starting' | 'running' | 'error' | 'stopped'; logs: string[] }>;
      generateApiKey: () => Promise<string>;
      saveApiKey: (apiKey: string) => Promise<{ success: boolean }>;
      saveBetfairCredentials: (credentials: { appKey: string; username: string; password: string }) => Promise<{ success: boolean }>;
      getApiPort: () => Promise<number | null>;
    };
  }
}

