.github/actions/run-smoke-test/action.ym:
name: 'Run 4-Step Diagnostic Smoke Test'
description: 'Installs an MSI, then runs a 4-step diagnostic to verify file installation, service status, port binding, and API health, finishing with a Paparazzi screenshot.'

inputs:
  msi-artifact-name:
    description: 'The name of the MSI artifact to download.'
    required: true
  service-name:
    description: 'The name of the Windows Service to verify (e.g., FortunaWebService).'
    required: true
  executable-path:
    description: 'The full, absolute path to the installed service executable to verify.'
    required: true
  port:
    description: 'The port to check for a listener.'
    required: true
  firewall-rule-name:
    description: 'The name of the firewall rule to create.'
    required: true

runs:
  using: "composite"
  steps:
    - name: 📥 Download MSI Artifact
      uses: actions/download-artifact@v4
      with:
        name: ${{ inputs.msi-artifact-name }}
        path: installer

    - name: 🛡️ Firewall & Install
      shell: pwsh
      run: |
        New-NetFirewallRule -DisplayName "${{ inputs.firewall-rule-name }}" -Direction Inbound -LocalPort ${{ inputs.port }} -Protocol TCP -Action Allow
        if (Get-Service -Name "${{ inputs.service-name }}" -ErrorAction SilentlyContinue) {
          sc.exe stop "${{ inputs.service-name }}" 2>&1 | Out-Null
          sc.exe delete "${{ inputs.service-name }}" 2>&1 | Out-Null
        }
        $msi = Get-ChildItem installer -Filter "*.msi" -Recurse | Select-Object -First 1
        if (!$msi) { throw "No MSI found" }
        Write-Host "Installing $($msi.Name)..."
        $msiPath = $msi.FullName
        $args = "/i `"$msiPath`" /qn /L*v installation.log"
        $proc = Start-Process msiexec.exe -ArgumentList $args -Wait -NoNewWindow -PassThru
        if ($proc.ExitCode -ne 0) {
          Get-Content installation.log -Tail 50
          throw "Install failed with code $($proc.ExitCode)"
        }

    - name: "🔍 Diag 1: Verify Install Directory"
      shell: pwsh
      run: |
        $expected = "${{ inputs.executable-path }}"
        if (Test-Path $expected) {
          Write-Host "✅ Binary FOUND at: $expected"
        } else {
          Write-Error "❌ Binary MISSING at: $expected"
          Write-Host "Listing C:\Program Files to see what happened:"
          Get-ChildItem "C:\Program Files" -Recurse -Depth 2 | Select-Object FullName
          exit 1
        }

    - name: "🔍 Diag 2: Check Windows Service Status"
      shell: pwsh
      run: |
        $svc = Get-Service "${{ inputs.service-name }}" -ErrorAction SilentlyContinue
        if (!$svc) {
          Write-Error "❌ Service '${{ inputs.service-name }}' is NOT registered! MSI Install failed to register service."
          exit 1
        }
        Write-Host "Service Status: $($svc.Status)"

        if ($svc.Status -ne 'Running') {
          Write-Host "⚠️ Service is not running. Attempting to start..."
          Start-Service "${{ inputs.service-name }}"
          Start-Sleep -Seconds 5
          $svc = Get-Service "${{ inputs.service-name }}"
        }

        if ($svc.Status -eq 'Running') {
          Write-Host "✅ Service is RUNNING"
        } else {
          Write-Error "❌ Service failed to start. Status: $($svc.Status)"
          Write-Host "Dumping Event Logs for Service Failure:"
          Get-EventLog -LogName System -Source "Service Control Manager" -Newest 20 | Format-Table -AutoSize
          exit 1
        }

    - name: "🔍 Diag 3: Port Listener Check"
      shell: pwsh
      run: |
        Write-Host "Starting Port Hunt on ${{ inputs.port }}..."
        $max = 30
        for ($i=1; $i -le $max; $i++) {
          $conn = Get-NetTCPConnection -LocalPort ${{ inputs.port }} -ErrorAction SilentlyContinue
          if ($conn) {
            Write-Host "✅ Port ${{ inputs.port }} is OPEN (State: $($conn.State))"
            exit 0
          }
          Write-Host "⏳ Attempt $i of $max: Port not open yet..."
          Start-Sleep -Seconds 2
        }
        Write-Error "❌ Timeout: Nothing is listening on Port ${{ inputs.port }} after 60 seconds."
        Write-Host "Checking for ANY relevant processes:"
        Get-Process | Where-Object { $_.ProcessName -match "fortuna|python" } | Format-Table Id, ProcessName, Responding
        exit 1

    - name: "🔍 Diag 4: HTTP Health Probe"
      shell: pwsh
      run: |
        try {
          $response = Invoke-WebRequest -Uri "http://localhost:${{ inputs.port }}/health" -UseBasicParsing
          if ($response.StatusCode -eq 200) {
            Write-Host "✅ HTTP 200 OK received. System is GREEN."
            Write-Host $response.Content
          } else {
            Write-Error "❌ HTTP Error: $($response.StatusCode)"
            exit 1
          }
        } catch {
          Write-Error "❌ HTTP Request Failed: $_"
          exit 1
        }

    - name: '📸 The Paparazzi (Visual Proof)'
      shell: pwsh
      run: |
        Write-Host "Installing Playwright..."
        npm install playwright
        npx playwright install chromium --with-deps

        $url = "http://127.0.0.1:${{ inputs.port }}/docs"

        node -e "
          const { chromium } = require('playwright');
          (async () => {
            try {
              const browser = await chromium.launch();
              const page = await browser.newPage();
              await page.goto('$url', { timeout: 15000 });
              await page.waitForSelector('.swagger-ui', { timeout: 5000 }).catch(() => console.log('UI not fully loaded, snapping anyway...'));
              await page.screenshot({ path: 'proof-of-life.png', fullPage: true });
              await browser.close();
            } catch (e) {
              console.error(e); process.exit(1);
            }
          })();
        "

    - name: Upload Visual Proof
      if: always()
      uses: actions/upload-artifact@v4
      with:
        name: visual-proof-${{ github.run_id }}
        path: proof-of-life.png

    - name: 🧹 Cleanup
      if: always()
      shell: pwsh
      run: |
        sc.exe stop ${{ inputs.service-name }}
        sc.exe delete ${{ inputs.service-name }}
        Remove-NetFirewallRule -DisplayName "${{ inputs.firewall-rule-name }}" -ErrorAction SilentlyContinue

electron/main.js:
// electron/main.js - CORRECTED VERSION
const { app, BrowserWindow, Tray, Menu, nativeImage, ipcMain, dialog } = require('electron');
const { autoUpdater } = require('electron-updater');
const { spawn } = require('child_process');
const net = require('net');
const path = require('path');
const fs = require('fs');
const SecureSettingsManager = require('./secure-settings-manager');

class FortunaDesktopApp {
 constructor() {
 this.backendProcess = null;
 this.mainWindow = null;
 this.tray = null;
 this.backendState = 'stopped'; // "stopped", "starting", "running", "error"
 this.backendLogs = [];
 this.isBackendStarting = false;
 }

 sendBackendStatusUpdate() {
 if (this.mainWindow) {
 this.mainWindow.webContents.send('backend-status-update', {
 state: this.backendState,
 logs: this.backendLogs.slice(-20) // Send last 20 log entries
 });
 }
 }

 stopBackend() {
 if (this.backendProcess && !this.backendProcess.killed) {
 console.log('Stopping backend process...');
 this.backendProcess.kill();
 this.backendState = 'stopped';
 this.isBackendStarting = false; // Ensure lock is released on stop
 this.backendLogs.push('Backend process stopped by user.');
 this.sendBackendStatusUpdate();
 }
 }

  checkPortInUse(port) {
    return new Promise((resolve, reject) => {
      const server = net.createServer();
      server.once('error', (err) => {
        if (err.code === 'EADDRINUSE') {
          resolve(true); // Port is in use
        } else {
          reject(err);
        }
      });
      server.once('listening', () => {
        server.close(() => {
          resolve(false); // Port is free
        });
      });
      server.listen(port, '127.0.0.1');
    });
  }

  async startBackend() {
    if (this.isBackendStarting) {
      console.log('Backend start already in progress. Ignoring request.');
      return;
    }

    this.isBackendStarting = true;
    this.backendState = 'starting';
    this.backendLogs = ['Attempting to start backend...'];
    this.sendBackendStatusUpdate();

    const port = process.env.FORTUNA_PORT || 8000;

    try {
      const isPortInUse = await this.checkPortInUse(port);
      if (isPortInUse) {
        console.log(`Port ${port} is already in use. Assuming backend is running.`);
        this.backendState = 'running';
        this.backendLogs.push(`Port ${port} is already in use. Assuming backend is running.`);
        this.isBackendStarting = false;
        this.sendBackendStatusUpdate();
        return; // The most important change: we just stop here.
      }
    } catch (error) {
        const errorMsg = `Error checking port ${port}: ${error.message}`;
        console.error(errorMsg);
        this.backendState = 'error';
        this.backendLogs.push(errorMsg);
        this.isBackendStarting = false;
        this.sendBackendStatusUpdate();
        dialog.showErrorBox('Network Error', `Could not check port ${port}. Please check your network configuration.`);
        return;
    }

    if (this.backendProcess && !this.backendProcess.killed) {
      console.log('An old backend process was found. Terminating it before starting a new one.');
      this.backendProcess.kill();
    }

    const isDev = !app.isPackaged;
    let backendCommand;
    const backendArgs = [];
    let backendCwd = process.cwd();

    if (isDev) {
      // This logic remains the same for development
      console.log('[DEV MODE] Configuring backend to run from Python venv...');
      backendCommand = path.join(__dirname, '..', '.venv', 'Scripts', 'python.exe');
      backendArgs.push('-m', 'uvicorn', 'api:app', '--host', '127.0.0.1', '--port', port);
      backendCwd = path.join(__dirname, '..', 'python_service');
    } else {
      console.log('[PROD MODE] Configuring backend to run from packaged executable...');
      backendCommand = path.join(process.resourcesPath, 'python-service-bin', 'fortuna-backend.exe');
    }

    if (!fs.existsSync(backendCommand)) {
      const errorMsg = `FATAL: Backend executable not found at ${backendCommand}`;
      console.error(errorMsg);
      this.backendState = 'error';
      this.backendLogs.push(errorMsg);
      this.isBackendStarting = false;
      this.sendBackendStatusUpdate();
      dialog.showErrorBox('Backend Missing', 'The backend service executable is missing. Please try reinstalling Fortuna Faucet.');
      return;
    }

    console.log(`Spawning backend: ${backendCommand} ${backendArgs.join(' ')}`);
    this.backendProcess = spawn(backendCommand, backendArgs, {
      cwd: backendCwd,
      env: { ...process.env, FORTUNA_PORT: port.toString() },
      stdio: ['ignore', 'pipe', 'pipe'],
    });

    this.backendProcess.stdout.on('data', (data) => {
      const output = data.toString().trim();
      console.log(`[Backend] ${output}`);
      this.backendLogs.push(output);
      if (this.backendState !== 'running' && output.includes('Application startup complete')) {
        console.log('✅ Backend is ready!');
        this.backendState = 'running';
        this.isBackendStarting = false;
      }
      this.sendBackendStatusUpdate();
    });

 this.backendProcess.stderr.on('data', (data) => {
 const errorOutput = data.toString().trim();
 console.error(`[Backend ERROR] ${errorOutput}`);
 this.backendLogs.push(`ERROR: ${errorOutput}`);
 this.backendState = 'error';
 this.isBackendStarting = false;
 this.sendBackendStatusUpdate();
 });

 this.backendProcess.on('error', (err) => {
 const errorMsg = `FATAL: Failed to start backend process: ${err.message}`;
 console.error(errorMsg);
 this.backendLogs.push(errorMsg);
 this.backendState = 'error';
 this.isBackendStarting = false;
 this.sendBackendStatusUpdate();
 });

 this.backendProcess.on('exit', (code) => {
 if (code !== 0 && this.backendState !== 'stopped') {
 const errorMsg = `Backend process exited unexpectedly with code ${code}`;
 console.error(errorMsg);
 this.backendLogs.push(errorMsg);
 this.backendState = 'error';
 this.isBackendStarting = false;
 this.sendBackendStatusUpdate();
 }
 });
 }

 getFrontendPath() {
 const isDev = !app.isPackaged;

 if (isDev) {
 return 'http://localhost:3000';
 }

 const indexPath = path.join(app.getAppPath(), 'web-ui-build', 'out', 'index.html');
 const { pathToFileURL } = require('url');
 return pathToFileURL(indexPath).toString();
 }

 createMainWindow() {
 this.mainWindow = new BrowserWindow({
 width: 1600,
 height: 1000,
 title: 'Fortuna Faucet - Racing Analysis',
 icon: path.join(__dirname, 'assets', 'icon.ico'),
 webPreferences: {
 nodeIntegration: false,
 contextIsolation: true,
 preload: path.join(__dirname, 'preload.js')
 },
 autoHideMenuBar: true,
 backgroundColor: '#1a1a2e'
 });

 const frontendUrl = this.getFrontendPath();
 this.mainWindow.loadURL(frontendUrl);

 if (!app.isPackaged) {
 this.mainWindow.webContents.openDevTools();
 }

 this.mainWindow.on('close', (event) => {
 if (!app.isQuitting) {
 event.preventDefault();
 this.mainWindow.hide();
 }
 });
 }

 createSystemTray() {
 // ... (rest of the file is unchanged)
 }

 initialize() {
  ipcMain.handle('get-api-port', () => {
    return process.env.FORTUNA_PORT || 8000;
  });
 this.createMainWindow();
 this.createSystemTray();
 this.startBackend();

 // Check for updates
 autoUpdater.checkForUpdatesAndNotify();

 autoUpdater.on('update-downloaded', (info) => {
 const dialogOpts = {
 type: 'info',
 buttons: ['Restart', 'Later'],
 title: 'Application Update',
 message: process.platform === 'win32' ? info.releaseName : info.releaseName,
 detail: 'A new version has been downloaded. Restart the application to apply the updates.'
 };

 dialog.showMessageBox(dialogOpts).then((returnValue) => {
 if (returnValue.response === 0) autoUpdater.quitAndInstall();
 });
 });

 ipcMain.on('restart-backend', () => this.startBackend());
 ipcMain.on('stop-backend', () => this.stopBackend());
 ipcMain.handle('get-backend-status', async () => ({
 state: this.backendState,
 logs: this.backendLogs.slice(-20)
 }));

 ipcMain.handle('get-api-key', async () => {
 return SecureSettingsManager.getApiKey();
 });

 ipcMain.handle('generate-api-key', async () => {
 const crypto = require('node:crypto');
 const newKey = crypto.randomBytes(16).toString('hex');
 SecureSettingsManager.saveApiKey(newKey);
 return newKey;
 });

 ipcMain.handle('save-api-key', async (event, apiKey) => {
 return SecureSettingsManager.saveApiKey(apiKey);
 });

 ipcMain.handle('save-betfair-credentials', async (event, credentials) => {
 return SecureSettingsManager.saveBetfairCredentials(credentials);
 });
 }

 cleanup() {
 if (this.backendProcess && !this.backendProcess.killed) {
 this.backendProcess.kill();
 }
 }
}

let fortunaApp;

app.whenReady().then(() => {
 fortunaApp = new FortunaDesktopApp();
 fortunaApp.initialize();
});

app.on('window-all-closed', () => {
 if (process.platform !== 'darwin') {
 // Do nothing, keep app running in tray
 }
});

app.on('activate', () => {
 if (BrowserWindow.getAllWindows().length === 0) {
 fortunaApp.createMainWindow();
 } else {
 fortunaApp.mainWindow.show();
 }
});

app.on('before-quit', () => {
 app.isQuitting = true;
 if (fortunaApp) {
 fortunaApp.cleanup();
 }
});

