@echo off
REM Interactive MSI installation with standard Windows UI

title Fortuna Faucet Installation Wizard

net session >nul 2>&1
if %errorlevel% neq 0 (
    echo ERROR: Administrator privileges required
    echo Please right-click this file and select "Run as Administrator"
    pause
    exit /b 1
)

REM Assumes the MSI is in the 'dist' subfolder relative to the project root
msiexec.exe /i "..\dist\Fortuna-Faucet-2.1.0-x64.msi" /L*v "%TEMP%\fortuna_install.log"

if %errorlevel% equ 0 (
    echo Installation completed successfully!
    echo Access dashboard at: http://localhost:3000
) else (
    echo Installation failed. Log: %TEMP%\fortuna_install.log
)
pause

scripts/repair_fortuna.bat:
@echo off
REM Repair corrupted or missing files

net session >nul 2>&1
if %errorlevel% neq 0 (
    echo ERROR: Admin rights required
    exit /b 1
)

echo Repairing Fortuna Faucet installation...

REM /f flag performs repair. Assumes MSI is in the 'dist' folder.
msiexec.exe /f "..\dist\Fortuna-Faucet-2.1.0-x64.msi" ^
    /qn ^
    /l*v "%TEMP%\fortuna_repair.log"

if %errorlevel% equ 0 (
    echo Repair completed successfully.
) else (
    echo Repair failed. Check log: %TEMP%\fortuna_repair.log
)

exit /b %errorlevel%

scripts/uninstall_fortuna.bat:
@echo off
REM Complete removal of Fortuna Faucet

net session >nul 2>&1
if %errorlevel% neq 0 (
    echo ERROR: Admin rights required
    exit /b 1
)

echo WARNING: This will remove Fortuna Faucet completely.
set /p confirm="Are you sure? (y/N): "

if /i not "%confirm%"=="y" exit /b 0

REM Find and remove MSI by UpgradeCode
for /f "tokens=2 delims=" %%A in ('wmic product where "Name like 'Fortuna Faucet%%'" get IdentifyingNumber /value') do (
    for /f "tokens=2 delims==" %%B in ("%%A") do (
        msiexec.exe /x %%B /qn /l*v "%TEMP%\fortuna_uninstall.log"
    )
)

REM Clean up directories
if exist "%PROGRAMFILES%\Fortuna Faucet" rmdir /s /q "%PROGRAMFILES%\Fortuna Faucet" 2>nul
if exist "%APPDATA%\Fortuna Faucet" rmdir /s /q "%APPDATA%\Fortuna Faucet" 2>nul

echo Uninstall complete.

scripts/validate_installation.ps1:
param (
    [string][Parameter(Mandatory=$true)] $InstallPath
)

function Write-Log {
    param ([string]$Message)
    # This log file is temporary and will be rolled back if the installation fails.
    # Its primary purpose is for debugging the installer itself.
    Add-Content -Path "$env:TEMP\fortuna_validation.log" -Value "$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss') - $Message"
}

try {
    Write-Log "--- Starting Installation Validation ---"
    Write-Log "Install Path: $InstallPath"

    $pythonExe = Join-Path $InstallPath "python\python.exe"
    Write-Log "Python Executable Path: $pythonExe"

    if (-not (Test-Path $pythonExe)) {
        Write-Log "[ERROR] python.exe not found at the expected location."
        # The script must exit with a non-zero code to trigger the MSI rollback.
        exit 1
    }

    Write-Log "Python executable found. Testing execution..."

    # Attempt to execute python.exe --version
    $process = Start-Process -FilePath $pythonExe -ArgumentList "--version" -Wait -PassThru -NoNewWindow

    if ($process.ExitCode -ne 0) {
        Write-Log "[ERROR] python.exe failed to execute correctly. Exit Code: $($process.ExitCode)"
        exit 1
    }

    Write-Log "Python execution successful. Validation passed."
    Write-Log "--- Validation Complete ---"

    # Exit with 0 for success
    exit 0
}
catch {
    Write-Log "[FATAL] An unexpected error occurred during validation: $_"
    # Exit with a non-zero code to signal failure to the installer
    exit 1
}
