    ALLUSERS=1 INSTALLSCOPE=perMachine

if ($LASTEXITCODE -eq 0) {
    Write-Host "✓ Silent deployment successful"
} else {
    Write-Host "✗ Silent deployment failed"
    Write-Host "Log: silent_test.log"
    exit 1
}

