# python_service/credentials_manager.py
try:
    import keyring

    # This check is crucial for cross-platform compatibility
    import keyring.backends.windows

    IS_WINDOWS = True
except ImportError:
    keyring = None
    IS_WINDOWS = False


class SecureCredentialsManager:
    """Manages secrets in the system's native credential store."""

    SERVICE_NAME = "Fortuna"

    @staticmethod
    def save_credential(account: str, secret: str) -> bool:
        """Saves a secret for a given account (e.g., 'api_key', 'betfair_username')."""
        if not IS_WINDOWS:
            print("Credential storage is only supported on Windows.")
            return False
        try:
            keyring.set_password(SecureCredentialsManager.SERVICE_NAME, account, secret)
            return True
        except Exception as e:
            print(f"❌ Failed to save credential for {account}: {e}")
            return False

    @staticmethod
    def get_credential(account: str) -> str:
        """Retrieves a secret for a given account."""
        if not IS_WINDOWS:
            return None
        try:
            return keyring.get_password(SecureCredentialsManager.SERVICE_NAME, account)
        except Exception as e:
            print(f"❌ Failed to retrieve credential for {account}: {e}")
            return None

    @staticmethod
    def get_betfair_credentials() -> tuple[str, str]:
        """Convenience method to retrieve both Betfair username and password."""
        username = SecureCredentialsManager.get_credential("betfair_username")
        password = SecureCredentialsManager.get_credential("betfair_password")
        return username, password

    @staticmethod
    def delete_credential(account: str):
        """Deletes a specific credential."""
        if not IS_WINDOWS:
            return
        try:
            keyring.delete_password(SecureCredentialsManager.SERVICE_NAME, account)
        except Exception:
            pass

python_service/health_check.py:
import socket
import sys


def is_port_available(port=8000):
    try:
        sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        result = sock.connect_ex(("127.0.0.1", port))
        sock.close()
        return result != 0
    except Exception:
        return False


if __name__ == "__main__":
    if not is_port_available(8000):
        print("ERROR: Port 8000 already in use. Kill existing process or use different port.")
        sys.exit(1)
    print("Port 8000 available ✓")

scripts/uninstall_fortuna.bat:
@echo off
REM Complete removal of Fortuna Faucet

net session >nul 2>&1
if %errorlevel% neq 0 (
    echo ERROR: Admin rights required
    exit /b 1
)

echo WARNING: This will remove Fortuna Faucet completely.
set /p confirm="Are you sure? (y/N): "

if /i not "%confirm%"=="y" exit /b 0

REM Find and remove MSI by UpgradeCode
for /f "tokens=2 delims=" %%A in ('wmic product where "Name like 'Fortuna Faucet%%'" get IdentifyingNumber /value') do (
    for /f "tokens=2 delims==" %%B in ("%%A") do (
        msiexec.exe /x %%B /qn /l*v "%TEMP%\fortuna_uninstall.log"
    )
)

REM Clean up directories
if exist "%PROGRAMFILES%\Fortuna Faucet" rmdir /s /q "%PROGRAMFILES%\Fortuna Faucet" 2>nul
if exist "%APPDATA%\Fortuna Faucet" rmdir /s /q "%APPDATA%\Fortuna Faucet" 2>nul

echo Uninstall complete.

