  const [betfairPassword, setBetfairPassword] = useState('');

  useEffect(() => {
    // Fetch the current API key when the component mounts
    const fetchApiKey = async () => {
      if (window.electronAPI?.getApiKey) {
        const key = await window.electronAPI.getApiKey();
        if (key) {
          setApiKey(key);
        }
      }
    };
    fetchApiKey();
  }, []);

