# python_service/etl.py
# ETL pipeline for populating the historical data warehouse

import json
import logging
import os
from datetime import date

import requests
from sqlalchemy import create_engine
from sqlalchemy import text
from sqlalchemy.exc import SQLAlchemyError

logging.basicConfig(level=logging.INFO)
logger = logging.getLogger(__name__)


class ScribesArchivesETL:
    def __init__(self):
        self.postgres_url = os.getenv("POSTGRES_URL")
        self.api_key = os.getenv("API_KEY")
        self.api_base_url = "http://localhost:8000"
        self.engine = self._get_db_engine()

    def _get_db_engine(self):
        if not self.postgres_url:
            logger.warning("POSTGRES_URL not set. ETL will be skipped.")
            return None
        try:
            return create_engine(self.postgres_url)
        except Exception as e:
            logger.error(f"Failed to create database engine: {e}", exc_info=True)
            return None

    def _fetch_race_data(self, target_date: date) -> list:
        """Fetches aggregated race data from the local API."""
        if not self.api_key:
            raise ValueError("API_KEY not found in environment.")

        url = f"{self.api_base_url}/api/races?race_date={target_date.isoformat()}"
        headers = {"X-API-KEY": self.api_key}
        response = requests.get(url, headers=headers, timeout=120)
        response.raise_for_status()
        return response.json().get("races", [])

    def _validate_and_transform(self, race: dict) -> tuple:
        """Validates a race dictionary and transforms it for insertion."""
        if not all(k in race for k in ["id", "venue", "race_number", "start_time", "runners"]):
            return (
                None,
                "Missing core fields (id, venue, race_number, start_time, runners)",
            )

        active_runners = [r for r in race.get("runners", []) if not r.get("scratched")]

        transformed = {
            "race_id": race["id"],
            "venue": race["venue"],
            "race_number": race["race_number"],
            "start_time": race["start_time"],
            "source": race.get("source"),
            "qualification_score": race.get("qualification_score"),
            "field_size": len(active_runners),
        }
        return transformed, None

    def run(self, target_date: date):
        if not self.engine:
            return

        logger.info(f"Starting ETL process for {target_date.isoformat()}...")
        try:
            races = self._fetch_race_data(target_date)
        except (requests.RequestException, ValueError) as e:
            logger.error(f"Failed to fetch race data: {e}", exc_info=True)
            return

        clean_records = []
        quarantined_records = []

        for race in races:
            transformed, reason = self._validate_and_transform(race)
            if transformed:
                clean_records.append(transformed)
            else:
                quarantined_records.append(
                    {
                        "race_id": race.get("id"),
                        "source": race.get("source"),
                        "payload": json.dumps(race),
                        "reason": reason,
                    }
                )

        with self.engine.connect() as connection:
            try:
                with connection.begin():  # Transaction block
                    if clean_records:
                        # Using ON CONFLICT to prevent duplicates
                        stmt = text(
                            """
                            INSERT INTO historical_races (
                                race_id, venue, race_number, start_time, source,
                                qualification_score, field_size
                            )
                            VALUES (
                                :race_id, :venue, :race_number, :start_time, :source,
                                :qualification_score, :field_size
                            )
                            ON CONFLICT (race_id) DO NOTHING;
                        """
                        )
                        connection.execute(stmt, clean_records)
                        logger.info(f"Inserted/updated {len(clean_records)} records into historical_races.")

                    if quarantined_records:
                        stmt = text(
                            """
                            INSERT INTO quarantined_races (race_id, source, payload, reason)
                            VALUES (:race_id, :source, :payload::jsonb, :reason);
                        """
                        )
                        connection.execute(stmt, quarantined_records)
                        logger.warning(f"Moved {len(quarantined_records)} records to quarantine.")
            except SQLAlchemyError as e:
                logger.error(f"Database transaction failed: {e}", exc_info=True)

        logger.info("ETL process finished.")


def run_etl_for_yesterday():
    from datetime import timedelta

    yesterday = date.today() - timedelta(days=1)
    etl = ScribesArchivesETL()
    etl.run(yesterday)

# python_service/middleware/error_handler.py

from fastapi import Request
from fastapi.exceptions import RequestValidationError
from fastapi.responses import JSONResponse

from ..user_friendly_errors import ERROR_MAP


class UserFriendlyException(Exception):
    def __init__(self, error_key: str, status_code: int = 500, details: str = None):
        self.error_key = error_key
        self.status_code = status_code
        self.details = details
        error_info = ERROR_MAP.get(error_key, ERROR_MAP["default"])
        self.message = error_info["message"]
        self.suggestion = error_info["suggestion"]
        super().__init__(self.message)


async def user_friendly_exception_handler(request: Request, exc: UserFriendlyException):
    return JSONResponse(
        status_code=exc.status_code,
        content={
            "error": {
                "message": exc.message,
                "suggestion": exc.suggestion,
                "details": exc.details,
            }
        },
    )


async def validation_exception_handler(request: Request, exc: RequestValidationError):
    """Convert Pydantic validation errors to user-friendly messages."""
    return JSONResponse(
        status_code=422,
        content={
            "detail": "Invalid request parameters",
            "errors": [
                {
                    "field": error["loc"][-1] if error["loc"] else "unknown",
                    "message": error["msg"],
                    "type": error["type"],
                }
                for error in exc.errors()
            ],
        },
    )

scripts/fortuna-quick-start.ps1:
# ====================================================================
# Fortuna Faucet - Quick Start Script (No Installation Required)
# ====================================================================
# This script runs Fortuna directly from source without any MSI
# Useful for development and testing before packaging
# ====================================================================

param(
    [switch]$SkipChecks,
    [switch]$NoFrontend
)

$ErrorActionPreference = 'Stop'
$OriginalLocation = Get-Location

# ============= CONFIGURATION =============
$PROJECT_ROOT = Split-Path -Parent $MyInvocation.MyCommand.Path
$VENV_PATH = Join-Path $PROJECT_ROOT ".venv"
$PYTHON_EXE = Join-Path $VENV_PATH "Scripts\python.exe"
$BACKEND_DIR = Join-Path $PROJECT_ROOT "python_service"
$FRONTEND_DIR = Join-Path $PROJECT_ROOT "web_platform\frontend"
$BACKEND_PORT = 8000
$FRONTEND_PORT = 3000

# ============= HELPER FUNCTIONS =============

function Write-Status {
    param([string]$Message, [string]$Status = "INFO")
    $Color = switch ($Status) {
        "OK"      { "Green" }
        "ERROR"   { "Red" }
        "WARNING" { "Yellow" }
        default   { "Cyan" }
    }
    Write-Host "[$Status] $Message" -ForegroundColor $Color
}

function Test-CommandExists {
    param([string]$Command)
    try {
        Get-Command $Command -ErrorAction Stop | Out-Null
        return $true
    } catch {
        return $false
    }
}

function Test-PortAvailable {
    param([int]$Port)
    try {
        $Listener = [System.Net.Sockets.TcpListener]::new([System.Net.IPAddress]::Any, $Port)
        $Listener.Start()
        $Listener.Stop()
        return $true
    } catch {
        return $false
    }
}

function Stop-ProcessOnPort {
    param([int]$Port)
    $Connection = Get-NetTCPConnection -LocalPort $Port -ErrorAction SilentlyContinue
    if ($Connection) {
        $ProcessId = $Connection.OwningProcess
        Write-Status "Killing process $ProcessId on port $Port" "WARNING"
        Stop-Process -Id $ProcessId -Force -ErrorAction SilentlyContinue
        Start-Sleep -Seconds 2
    }
}

function Wait-ForBackend {
    param([int]$MaxAttempts = 30)

    Write-Status "Waiting for backend to start (http://127.0.0.1:$BACKEND_PORT/health)..."

    for ($i = 1; $i -le $MaxAttempts; $i++) {
        try {
            $Response = Invoke-WebRequest -Uri "http://127.0.0.1:$BACKEND_PORT/health" -UseBasicParsing -TimeoutSec 2
            if ($Response.StatusCode -eq 200) {
                Write-Status "Backend is healthy!" "OK"
                return $true
            }
        } catch {
            Write-Host "." -NoNewline
            Start-Sleep -Seconds 1
        }
    }

    Write-Status "Backend failed to start after $MaxAttempts seconds" "ERROR"
    return $false
}

# ============= PREFLIGHT CHECKS =============

Write-Host "`n========================================" -ForegroundColor Cyan
Write-Host " Fortuna Faucet - Quick Start" -ForegroundColor Cyan
Write-Host "========================================`n" -ForegroundColor Cyan

if (-not $SkipChecks) {
    Write-Status "Running preflight checks..."

    # Check Python
    if (-not (Test-Path $PYTHON_EXE)) {
        Write-Status "Python virtual environment not found at $VENV_PATH" "ERROR"
        Write-Status "Please run setup script first or create venv manually" "ERROR"
        exit 1
    }
    Write-Status "Python venv found" "OK"

    # Check Node.js
    if (-not (Test-CommandExists "node")) {
        Write-Status "Node.js not found in PATH" "ERROR"
        Write-Status "Install from: https://nodejs.org/" "ERROR"
        exit 1
    }
    Write-Status "Node.js found: $(node --version)" "OK"

    # Check npm
    if (-not (Test-CommandExists "npm")) {
        Write-Status "npm not found" "ERROR"
        exit 1
    }
    Write-Status "npm found: $(npm --version)" "OK"

    # Check if ports are available
    if (-not (Test-PortAvailable $BACKEND_PORT)) {
        Write-Status "Port $BACKEND_PORT is already in use" "WARNING"
        Stop-ProcessOnPort $BACKEND_PORT
    }

    if (-not $NoFrontend -and -not (Test-PortAvailable $FRONTEND_PORT)) {
        Write-Status "Port $FRONTEND_PORT is already in use" "WARNING"
        Stop-ProcessOnPort $FRONTEND_PORT
    }

    # Check Python dependencies
    Write-Status "Checking Python dependencies..."
    $PipList = & $PYTHON_EXE -m pip list
    if ($PipList -notmatch "fastapi") {
        Write-Status "Python dependencies not installed" "WARNING"
        Write-Status "Installing dependencies..."
        & $PYTHON_EXE -m pip install -r (Join-Path $BACKEND_DIR "requirements.txt")
    } else {
        Write-Status "Python dependencies OK" "OK"
    }

    # Check Node dependencies
    if (-not $NoFrontend) {
        Write-Status "Checking Node.js dependencies..."
        $NodeModules = Join-Path $FRONTEND_DIR "node_modules"
        if (-not (Test-Path $NodeModules)) {
            Write-Status "Node.js dependencies not installed" "WARNING"
            Write-Status "Installing dependencies..."
            Push-Location $FRONTEND_DIR
            npm install
            Pop-Location
        } else {
            Write-Status "Node.js dependencies OK" "OK"
        }
    }

    Write-Host ""
}

# ============= LAUNCH BACKEND =============

Write-Status "Starting backend server..."

$BackendJob = Start-Job -ScriptBlock {
    param($PythonExe, $BackendDir)
    Set-Location $BackendDir
    & $PythonExe -m uvicorn api:app --host 127.0.0.1 --port 8000 --reload
} -ArgumentList $PYTHON_EXE, $BACKEND_DIR

Write-Status "Backend job started (ID: $($BackendJob.Id))"

# Wait for backend to be healthy
if (-not (Wait-ForBackend)) {
    Write-Status "Backend startup failed. Checking logs..." "ERROR"
    Receive-Job $BackendJob
    Stop-Job $BackendJob
    Remove-Job $BackendJob
    exit 1
}

# ============= LAUNCH FRONTEND =============

if (-not $NoFrontend) {
    Write-Status "Starting frontend dev server..."

    $FrontendJob = Start-Job -ScriptBlock {
        param($FrontendDir)
        Set-Location $FrontendDir
        npm run dev
    } -ArgumentList $FRONTEND_DIR

    Write-Status "Frontend job started (ID: $($FrontendJob.Id))"

    # Wait a bit for frontend to start
    Start-Sleep -Seconds 5

    Write-Status "Opening browser..." "OK"
    Start-Process "http://localhost:$FRONTEND_PORT"
}

# ============= MONITORING =============

Write-Host "`n========================================" -ForegroundColor Green
Write-Host " Fortuna is now running!" -ForegroundColor Green
Write-Host "========================================" -ForegroundColor Green
Write-Host ""
Write-Status "Backend:  http://127.0.0.1:$BACKEND_PORT" "OK"
if (-not $NoFrontend) {
    Write-Status "Frontend: http://127.0.0.1:$FRONTEND_PORT" "OK"
}
Write-Host ""
Write-Host "Press Ctrl+C to stop all services" -ForegroundColor Yellow
Write-Host ""

try {
    while ($true) {
        Start-Sleep -Seconds 2

        # Check if jobs are still running
        if ($BackendJob.State -eq "Failed" -or $BackendJob.State -eq "Stopped") {
            Write-Status "Backend has stopped unexpectedly!" "ERROR"
            Receive-Job $BackendJob
            break
        }

        if (-not $NoFrontend -and ($FrontendJob.State -eq "Failed" -or $FrontendJob.State -eq "Stopped")) {
            Write-Status "Frontend has stopped unexpectedly!" "ERROR"
            Receive-Job $FrontendJob
            break
        }
    }
} finally {
    # ============= CLEANUP =============
    Write-Host "`n`nShutting down..." -ForegroundColor Yellow

    if ($BackendJob) {
        Write-Status "Stopping backend..."
        Stop-Job $BackendJob -ErrorAction SilentlyContinue
        Remove-Job $BackendJob -Force -ErrorAction SilentlyContinue
    }

    if ($FrontendJob) {
        Write-Status "Stopping frontend..."
        Stop-Job $FrontendJob -ErrorAction SilentlyContinue
        Remove-Job $FrontendJob -Force -ErrorAction SilentlyContinue
    }

    # Kill any remaining processes on the ports
    Stop-ProcessOnPort $BACKEND_PORT
    if (-not $NoFrontend) {
        Stop-ProcessOnPort $FRONTEND_PORT
    }

    Set-Location $OriginalLocation
    Write-Status "Cleanup complete" "OK"
}

# ============= USAGE EXAMPLES =============
<#
.SYNOPSIS
Quick start script for Fortuna Faucet (no installation required)

.DESCRIPTION
Launches the backend and frontend servers directly from source code
Useful for development and testing before creating an MSI installer

.PARAMETER SkipChecks
Skip all preflight dependency checks (faster startup)

.PARAMETER NoFrontend
Only start the backend API server (no UI)

.EXAMPLE
.\fortuna-quick-start.ps1
Starts both backend and frontend with full checks

.EXAMPLE
.\fortuna-quick-start.ps1 -NoFrontend
Starts only the backend API (useful for API testing)

.EXAMPLE
.\fortuna-quick-start.ps1 -SkipChecks
Fast startup (assumes all dependencies are already installed)
#>

scripts/uninstall_fortuna.bat:
@echo off
REM Complete removal of Fortuna Faucet

net session >nul 2>&1
if %errorlevel% neq 0 (
    echo ERROR: Admin rights required
    exit /b 1
)

echo WARNING: This will remove Fortuna Faucet completely.
set /p confirm="Are you sure? (y/N): "

if /i not "%confirm%"=="y" exit /b 0

REM Find and remove MSI by UpgradeCode
for /f "tokens=2 delims=" %%A in ('wmic product where "Name like 'Fortuna Faucet%%'" get IdentifyingNumber /value') do (
    for /f "tokens=2 delims==" %%B in ("%%A") do (
        msiexec.exe /x %%B /qn /l*v "%TEMP%\fortuna_uninstall.log"
    )
)

REM Clean up directories
if exist "%PROGRAMFILES%\Fortuna Faucet" rmdir /s /q "%PROGRAMFILES%\Fortuna Faucet" 2>nul
if exist "%APPDATA%\Fortuna Faucet" rmdir /s /q "%APPDATA%\Fortuna Faucet" 2>nul

echo Uninstall complete.

