# Dedicated test suite for the TrifectaAnalyzer, resurrected and expanded.
from datetime import datetime

import pytest

from python_service.analyzer import TrifectaAnalyzer
from python_service.models import Race
from python_service.models import Runner


@pytest.fixture
def analyzer():
    return TrifectaAnalyzer()


@pytest.fixture
def runners():
    return []


@pytest.fixture
def create_race(runners):
    return Race(
        id="test-race",
        venue="TEST",
        race_number=1,
        start_time=datetime.now(),
        runners=runners,
        source="test",
