EXCLUDED_DIRS = ['.git', '.venv', 'node_modules', 'build', 'dist', '__pycache__', 'ReviewableJSON']
EXCLUDED_FILES = ['audit_rebranding.py', 'REBRANDING_AUDIT.md']
