    """Searches a single file for a list of terms, case-insensitively."""
    try:
        with open(file_path, 'r', encoding='utf-8', errors='ignore') as f:
            content = f.read().lower()
            for term in terms:
                if term in content:
                    return True
    except Exception as e:
