  private db: Database | null = null;

  private async getDb(): Promise<Database> {
    if (!this.db) {
      const dbPath = process.env.FORTUNA_DB_PATH || path.join(process.cwd(), '../../../../shared_database/races.db');
      this.db = await open({
        filename: dbPath,
        driver: sqlite3.Database
      });
    }
    return this.db;
