// electron/secure-settings-manager.js
const { app } = require('electron');
const fs = require('fs');
const path = require('path');

const SETTINGS_FILE = path.join(app.getPath('userData'), 'settings.json');

class SecureSettingsManager {
 constructor() {
 this.settings = this.loadSettings();
 }

 loadSettings() {
 try {
 if (fs.existsSync(SETTINGS_FILE)) {
 const data = fs.readFileSync(SETTINGS_FILE, 'utf-8');
 return JSON.parse(data);
 }
 } catch (error) {
 console.error('Error loading settings:', error);
 }
 return {};
 }

 saveSettings() {
 try {
 fs.writeFileSync(SETTINGS_FILE, JSON.stringify(this.settings, null, 2));
 } catch (error) {
 console.error('Error saving settings:', error);
 }
 }

 getApiKey() {
 return this.settings.apiKey || null;
 }

 saveApiKey(apiKey) {
 this.settings.apiKey = apiKey;
 this.saveSettings();
 return { success: true };
 }

 getBetfairCredentials() {
 return this.settings.betfair || null;
 }

 saveBetfairCredentials(credentials) {
 this.settings.betfair = credentials;
 this.saveSettings();
 return { success: true };
 }
}

module.exports = new SecureSettingsManager();

# python_service/middleware/error_handler.py

from fastapi import Request
from fastapi.exceptions import RequestValidationError
from fastapi.responses import JSONResponse

from ..user_friendly_errors import ERROR_MAP


class UserFriendlyException(Exception):
    def __init__(self, error_key: str, status_code: int = 500, details: str = None):
        self.error_key = error_key
        self.status_code = status_code
        self.details = details
        error_info = ERROR_MAP.get(error_key, ERROR_MAP["default"])
        self.message = error_info["message"]
        self.suggestion = error_info["suggestion"]
        super().__init__(self.message)


async def user_friendly_exception_handler(request: Request, exc: UserFriendlyException):
    return JSONResponse(
        status_code=exc.status_code,
        content={
            "error": {
                "message": exc.message,
                "suggestion": exc.suggestion,
                "details": exc.details,
            }
        },
    )


async def validation_exception_handler(request: Request, exc: RequestValidationError):
    """Convert Pydantic validation errors to user-friendly messages."""
    return JSONResponse(
        status_code=422,
        content={
            "detail": "Invalid request parameters",
            "errors": [
                {
                    "field": error["loc"][-1] if error["loc"] else "unknown",
                    "message": error["msg"],
                    "type": error["type"],
                }
                for error in exc.errors()
            ],
        },
    )
