          <div className="space-y-3">
            <input
              type="password"
              placeholder="App Key"
              value={betfairAppKey}
              onChange={(e) => setBetfairAppKey(e.target.value)}
              className="w-full p-2 bg-slate-700 rounded border border-slate-600 placeholder-slate-500"
            />
